package PracticeProgram;

public class PrintCharToNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(char c = 'a';c<='z';c++){
			System.out.print(c +" ");
		}
		System.out.println();
		for(char c = 'a';c<='z';c++){
			System.out.print((int)c +" ");
		}
	}

}
